import React from "react";
import styled from "styled-components";

const ModalRegister = ({children, state, changestate}) =>{
    return(
        <>
        {state &&
            <Overlay>
                <Content_Modal>
                    <Buton_close onClick={() => changestate(false)}>

                    </Buton_close>
                    {children}
                </Content_Modal>
            </Overlay>
        }
        </>
    );
}
export default ModalRegister;

const Overlay = styled.div`
    width: 100%;
    height: 100vh;
    position: fixed;
    top: 0;
    left: 0;
    background: rgba(154, 153, 153, 0.49);
    padding: 40px;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const Content_Modal = styled.div`
    width: 500px;
    height: 35rem;
    background: #000;
    position: relative;
    border-radius: 5px;
    box-shadow: rgba(100,100,111,0.2) 0px 7px 29px 0px;
    padding: 20px;
`;

const Buton_close = styled.button`
    position: absolute;
    top: 20px;
    right: 20px;
    width: 40px;
    height: 40px;
    border: none;
    background: none;
    cursor: pointer;
    transition: .3 ease all;
    border-radius: 5px;
    color: #1766DC;

    &:hover {
        background: #f2f2f2;
    }

    svg {
        width: 100%;
        height: 100%;
        margin-top: 0.2rem;
    }
`;
