import { useRef, useState, useEffect, useContext } from 'react';
import AuthContext from "../../../context/AuthProvider";
import ModalLogin from "./ModalLogin";
import styled from 'styled-components';
import axios from '../../../api/axios';
import { Link } from 'react-router-dom';
const LOGIN_URL = '/auth';

const Login = () => {
    const { setAuth } = useContext(AuthContext);
    const userRef = useRef();
    const errRef = useRef();

    const [stateModal, changeStateModal] = useState(false);

    const [user, setUser] = useState('');
    const [pwd, setPwd] = useState('');
    const [errMsg, setErrMsg] = useState('');
    const [success, setSuccess] = useState(false);

    useEffect(() => {
        setErrMsg('');
    }, [user, pwd])

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post(LOGIN_URL,
                JSON.stringify({ user, pwd }),
                {
                    headers: { 'Content-Type': 'application/json' },
                    withCredentials: true
                }
            );
            console.log(JSON.stringify(response?.data));
            const accessToken = response?.data?.accessToken;
            const roles = response?.data?.roles;
            setAuth({ user, pwd, roles, accessToken });
            setUser('');
            setPwd('');
            setSuccess(true);
        } catch (err) {
            if (!err?.response) {
                setErrMsg('No Server Response');
            } else if (err.response?.status === 400) {
                setErrMsg('Missing Username or Password');
            } else if (err.response?.status === 401) {
                setErrMsg('Unauthorized');
            } else {
                setErrMsg('Login Failed');
            }
            errRef.current.focus();
        }
    }

    return (
        <div>
            <Content_Anchor>
                <Anchor onClick={() => changeStateModal(!stateModal)}>Ingresar</Anchor>
            </Content_Anchor>

            <ModalLogin state={stateModal} changestate={changeStateModal}>
                <Content>
                <>
            {success ? (
                <section>
                    <h1>¡Estás conectado!</h1>
                    <br />
                    <p>
                        <a href="/home">Volver al Inicio</a>
                    </p>
                </section>
            ) : (
                <section>
                    <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
                    <h1>Sign In</h1>
                    <form onSubmit={handleSubmit}>
                        <label htmlFor="username">Usuario:</label>
                        <input
                            type="text"
                            id="username"
                            ref={userRef}
                            autoComplete="off"
                            onChange={(e) => setUser(e.target.value)}
                            value={user}
                            required
                        />

                        <label htmlFor="password">Contraseña:</label>
                        <input
                            type="password"
                            id="password"
                            onChange={(e) => setPwd(e.target.value)}
                            value={pwd}
                            required
                        />
                        <button>Sign In</button>
                    </form>
                    <p>
                    ¿Necesitas una cuenta?<br />
                        <span className="line">
                        <Buton_Register>
                            REGIS
                        </Buton_Register>
                        </span>
                    </p>
                </section>
            )}
        </>
                </Content>
            </ModalLogin>
        </div>

    )
}

export default Login;

const Content_Anchor = styled.div`
    font-family: 'Bruno Ace SC', cursive;
    padding: 3px;
`;

const Anchor = styled.div`
    color: black;
    cursor: pointer;
    padding: 3px;
`;

const Content = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
    font-family: 'Bruno Ace SC', cursive;
`;
